CREATE DATABASE IF NOT EXISTS `log_comments` DEFAULT CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci;

CREATE TABLE `comments` (
                            `id` int NOT NULL,
                            `text` varchar(60) NOT NULL,
                            `positionX` int NOT NULL,
                            `positionY` int NOT NULL,
                            `color` varchar(20) NOT NULL,
                            `time` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
                            `utm` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;


CREATE TABLE `links` (
                         `id` int NOT NULL,
                         `row_num` int NOT NULL,
                         `columns_num` int NOT NULL,
                         `html` mediumtext COLLATE utf8mb3_unicode_ci NOT NULL,
                         `href` text COLLATE utf8mb3_unicode_ci NOT NULL,
                         `max_columns` int NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_unicode_ci;


CREATE TABLE `link_media` (
                              `id` int NOT NULL,
                              `max_width` int NOT NULL,
                              `sizes_data` mediumtext COLLATE utf8mb3_unicode_ci NOT NULL,
                              `columns` int NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_unicode_ci;


CREATE TABLE `utm` (
                       `id` int NOT NULL,
                       `utm` varchar(255) NOT NULL,
                       `time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
                       `ip` varchar(255) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;


CREATE TABLE `utm_items` (
                             `id` int NOT NULL,
                             `utm_desc` varchar(1000) CHARACTER SET utf8mb3 COLLATE utf8mb3_unicode_ci DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_unicode_ci;


